import React, { Component } from "react";
import {Table, Form, Button} from 'react-bootstrap';

export default class Schedule1 extends Component {


  constructor(props){
    super(props);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.state = { confirmation:false,
      crewSchedule: [], // list is empty in the beginning
          error: false};
  }

  handleSubmit(event){
    this.setState({...this.state, confirmation:true});
  }


 componentDidMount() {
     this.getUserList(); // function call
 }

 getUserList = async () => {
     try { //try to get data
         const response = await fetch("http://ec2-3-87-118-108.compute-1.amazonaws.com:8080/retrieveSchedule", {
         mode: 'cors', // no-cors, *cors, same-origin
        //  cache: 'no-cache', // *default, no-cache, reload, force-cache, only-if-cached
        //  credentials: 'same-origin', // include, *same-origin, omit
         headers: {
           'Content-Type': 'application/json',
          //  'Access-Control-Allow-Origin': 'http://localhost:3000'
          'Access-Control-Allow-Origin': '*'

           // 'Content-Type': 'application/x-www-form-urlencoded',
         },
        //  redirect: 'follow', // manual, *follow, error
        //  referrerPolicy: 'no-referrer', // no-referrer, *no-referrer-when-downgrade, origin, origin-when-cross-origin, same-origin, strict-origin, strict-origin-when-cross-origin, unsafe-url
        //  body: JSON.stringify(data) // body data type must match "Content-Type" header
       });
         if (response.ok) { // ckeck if status code is 200
             const data = await response.json();
             console.log("DATA:.......",data);
             this.setState({...this.state, crewSchedule: data});
         } else { this.setState({...this.state, error: true }) }
     } catch (e) { //code will jump here if there is a network problem
 this.setState({ ...this.state, error: true });
 console.log(e);

}
};

    render() {

 return (

<div className="schedule1-component m-4 p-4">
{
  this.state.confirmation && <span style={{ color: 'blue' }} > schedule(s) broadcasted !</span> }

<h2 id="crewDashBoardTitle">Schedule Suggestion 1</h2>
 <Table striped bordered hover size="sm">
  <thead>
    <tr>
      <th>Employee</th>
      <th>Monday</th>
      <th>Tuesday</th>
      <th>Wednesday</th>
      <th>Thursday</th>
      <th>Friday</th>
      <th>Saturday</th>
      <th>Sunday</th>
    </tr>
  </thead>
  <tbody>


 {/* <div> */}

  {this.state.crewSchedule && this.state.crewSchedule.map(schedule => (
    <tr key={schedule}>

      <td>{schedule.name}</td>
      <td>{schedule.mon}</td>
      <td>{schedule.tues} </td>
      <td> {schedule.wed} </td>
      <td> {schedule.thrus} </td>
      <td> {schedule.fri} </td>
      <td> {schedule.sat} </td>
      <td> {schedule.sun} </td>

      </tr>
            ))}

    {/* </div> */}



  </tbody>
 </Table> 
 <Button variant="primary" type="submit" onClick={this.handleSubmit}>
    Confirm Schedule
  </Button>
</div>
);
    }
}
